(()=>{"use strict";new class{constructor(){this.initializeElements(),this.loadSettings(),this.initializeEventListeners()}initializeElements(){this.delayMinInput=document.getElementById("delayMin"),this.delayMaxInput=document.getElementById("delayMax"),this.maxProfilesInput=document.getElementById("maxProfiles"),this.saveButton=document.getElementById("saveBtn"),this.statusDiv=document.getElementById("status"),this.fieldCheckboxes={name:document.getElementById("fieldName"),title:document.getElementById("fieldTitle"),company:document.getElementById("fieldCompany"),location:document.getElementById("fieldLocation"),profileUrl:document.getElementById("fieldProfileUrl"),linkedInUrl:document.getElementById("fieldLinkedInUrl")}}initializeEventListeners(){this.saveButton.addEventListener("click",(()=>this.saveSettings()))}async loadSettings(){try{const t=(await chrome.storage.local.get("scrapingConfig")).scrapingConfig;t&&(this.delayMinInput.value=t.delayMin.toString(),this.delayMaxInput.value=t.delayMax.toString(),this.maxProfilesInput.value=t.maxProfiles.toString(),t.fieldsToExtract.forEach((t=>{this.fieldCheckboxes[t]&&(this.fieldCheckboxes[t].checked=!0)})))}catch(t){console.error("Error loading settings:",t),this.showStatus("Error loading settings",!1)}}async saveSettings(){try{const t={delayMin:parseInt(this.delayMinInput.value),delayMax:parseInt(this.delayMaxInput.value),maxProfiles:parseInt(this.maxProfilesInput.value),fieldsToExtract:Object.entries(this.fieldCheckboxes).filter((([t,e])=>e.checked)).map((([t])=>t))};await chrome.storage.local.set({scrapingConfig:t}),this.showStatus("Settings saved successfully",!0)}catch(t){console.error("Error saving settings:",t),this.showStatus("Error saving settings",!1)}}showStatus(t,e){this.statusDiv.textContent=t,this.statusDiv.className="status "+(e?"success":"error"),setTimeout((()=>{this.statusDiv.className="status"}),3e3)}}})();